INSERT INTO `products` (`ID`, `Name`, `Price`, `Discount`, `Image`, `Description`, `Detail`, `Quantity`, `Status`, `Views`, `Category_ID`) VALUES
(1, 'Dây Dắt Họa Tiết Cho Chó Mèo', 70000.00, 66000.00, 'https://paddy.vn/cdn/shop/files/day-dat-kem-vong-co-hoa-tiet-cho-cho-meo.png', 'Dây Dắt Kèm Vòng Cổ Nhiều Kiểu Cho Chó Mèo <8kg\r\n\r\nCHẤT LIỆU\r\n+ Dây được làm từ sợi vải loại 1 rất bền chắc.\r\n+ Khóa chốt bằng inox không han không rỉ.', 'MÀU SẮC: GIAO NGẪU NHIÊN\r\n\r\nĐẶC ĐIỂM NỔI BẬT:\r\n- Chất liệu cho độ bền cao.\r\n- Thiết kế dày dặn và chắc chắn tạo sự êm ái khi đeo vào vai thú cưng\r\n- Giúp bạn giữ chặt và theo sát các Boss của mình.\r\n- Màu sắc nổi bật và thời trang tạo điểm nhấn nổi bật cho thú cưng.\r\n- Thiết kế khóa theo từng nấc giúp bạn dễ dàng đeo, tháo mở vòng và chỉnh độ to nhỏ để vừa vặn với thú cưng một cách nhanh chóng.\r\n- Kiểu dáng và màu sắc đẹp và khỏe khoắn, hấp dẫn thú cưng nhà bạn.', 10, 1, 50, 6),
(2, 'Set Dây Dắt Kèm Vòng Yếm Vải In Họa Tiết Cho Chó Mèo\r\n', 70000.00, 55000.00, 'https://paddy.vn/cdn/shop/files/day-dat-kem-vong-cho-cho-meo.png', 'LOẠI DÀY 1CM\r\n+ Phù hợp với chó mèo <6kg\r\n+ Dây dắt: 105cm x dày 1cm\r\n+ Yếm: Vòng ngực từ 23 - 38cm\r\n\r\nLOẠI DÀY 1.5CM\r\n+ Phù hợp với chó mèo <12kg\r\n+ Dây dắt: 105cm x dày 1.5cm\r\n+ Yếm: Vòng ngực từ 35 - 60cm', 'Set Dây Dắt Kèm Vòng Yếm Vải In Họa Tiết Cho Chó Mèo\r\n\r\nMÀU SẮC: GIAO NGẪU NHIÊN\r\n\r\n- Chất liệu Dây Cho Chó Mèo rất chắc chắn và thiết kế dày dặn cho độ bền cao, đồng thời tạo cảm giác êm ái khi đeo.\r\n- Dạng yếm ôm sát có thể thay đổi kích cỡ, cực kỳ chắc chắn. Bạn quản lý các Boss hiếu động và tinh nghịch dễ hơn.\r\n- Thiết kế Dây Dắt Yếm 1cm Cho Chó Mèo có khóa bấm giúp bạn dễ dàng đeo hoặc tháo mở dây và vòng một cách nhanh chóng\r\n- Kiểu dáng và màu sắc đẹp, hấp dẫn thú cưng nhà bạn, chất liệu bền, chắc chắn.\r\n\r\nKÍCH THƯỚC\r\nSản phẩm Dây Dắt Yếm Cho Chó Mèo có kích thước nhỏ gọn, phù hợp với mọi giống chó mèo dưới 12kg. Sản phẩm có thể điều chỉnh được kích cỡ để vừa vặn với các \"boss\". Đảm bảo các chú chó không bị đau, siết cổ khi sử dụng sản phẩm.', 20, 1, 20, 6),
(3, 'Áo Cho Chó Mèo Trắng Đỏ Bunny Vải Nỉ', 60000.00, 50000.00, 'https://paddy.vn/cdn/shop/files/quan-ao-cho-meo_1.png', 'Áo Cho Chó Mèo Trắng Đỏ Bunny Vải Nỉ\r\n\r\n🐾 CHẤT LIỆU\r\n- Chất vải nỉ êm ái, ấm áp cho thú cưng\r\n\r\n🐾 GỢI Ý CHỌN SIZE\r\nRỘNG NGỰC*DÀI LƯNG (Trọng lượng của bé)\r\n- S: 25cm*35cm (1~2.5kg)\r\n- M: 30cm*40cm (2.5~4kg)\r\n- L: 35cm*45cm (4~5kg)\r\n- XL: 40cm*50cm (5~7kg)', 'Áo Cho Chó Mèo Trắng Đỏ Bunny Vải Nỉ\r\n\r\n🐾 CHẤT LIỆU\r\n- Chất vải nỉ êm ái, ấm áp cho thú cưng\r\n\r\n🐾 GỢI Ý CHỌN SIZE\r\nRỘNG NGỰC*DÀI LƯNG (Trọng lượng của bé)\r\n- S: 25cm*35cm (1~2.5kg)\r\n- M: 30cm*40cm (2.5~4kg)\r\n- L: 35cm*45cm (4~5kg)\r\n- XL: 40cm*50cm (5~7kg)\r\n\r\n\r\n***Với áo lẻ: tham khảo độ rộng vòng ngực\r\n***Với set áo kèm quần: tham khảo độ dài lưng\r\n\r\n🐾 Lưu ý: Số liệu trên chỉ mang tính chất tương đối. Size áo còn phụ thuộc vào giống cún và độ dày lông. Để chọn chính xác cỡ áo cho chó mèo, bạn vui lòng chat trực tiếp cho shop để được tư vấn tốt nhất.\r\n\r\n🐾 CHÍNH SÁCH ĐỔI HÀNG\r\n- Nếu sản phẩm bị lỗi do nhà sản xuất, shop sẽ gửi bù cho bạn và shop sẽ chịu toàn bộ chi phí\r\n- Nếu bạn muốn đổi size, vui lòng gửi lại hàng ra cho chúng mình, chúng mình sẽ gửi lại size muốn đổi cho bạn, bạn thanh toán phí ship', 100, 1, 8, 3),
(4, 'Áo Cho Chó Vải Lông Hình Gấu Thỏ\r\n', 100000.00, 90000.00, 'https://paddy.vn/cdn/shop/products/309717473_1158685708406242_943556808542293635_n.jpg', 'Áo Cho Chó Mèo Vải Lông Hình Gấu Thỏ\r\n\r\n🐾 CHẤT LIỆU\r\n- Chất vải lông êm ái, ấm áp cho thú cưng\r\n\r\n🐾 GỢI Ý CHỌN SIZE\r\nRỘNG NGỰC*DÀI LƯNG (Trọng lượng của bé)\r\n- S: 25cm*35cm (1~2.5kg)\r\n- M: 30cm*40cm (2.5~4kg)\r\n- L: 35cm*45cm (4~5kg)\r\n- XL: 40cm*50cm (5~7kg)', 'Áo Cho Chó Mèo Vải Lông Hình Gấu Thỏ\r\n\r\n🐾 CHẤT LIỆU\r\n- Chất vải lông êm ái, ấm áp cho thú cưng\r\n\r\n🐾 GỢI Ý CHỌN SIZE\r\nRỘNG NGỰC*DÀI LƯNG (Trọng lượng của bé)\r\n- S: 25cm*35cm (1~2.5kg)\r\n- M: 30cm*40cm (2.5~4kg)\r\n- L: 35cm*45cm (4~5kg)\r\n- XL: 40cm*50cm (5~7kg)\r\n\r\n\r\n***Với áo lẻ: tham khảo độ rộng vòng ngực\r\n***Với set áo kèm quần: tham khảo độ dài lưng\r\n\r\n🐾 Lưu ý: Số liệu trên chỉ mang tính chất tương đối. Size áo còn phụ thuộc vào giống cún và độ dày lông. Để chọn chính xác cỡ áo cho chó mèo, bạn vui lòng chat trực tiếp cho shop để được tư vấn tốt nhất.\r\n\r\n🐾 CHÍNH SÁCH ĐỔI HÀNG\r\n- Nếu sản phẩm bị lỗi do nhà sản xuất, shop sẽ gửi bù cho bạn và shop sẽ chịu toàn bộ chi phí\r\n- Nếu bạn muốn đổi size, vui lòng gửi lại hàng ra cho chúng mình, chúng mình sẽ gửi lại size muốn đổi cho bạn, bạn thanh toán phí ship', 100, 1, 20, 3),
(5, 'Áo Dài Tết Cho Chó Mèo Vải Lụa\r\n', 120000.00, 100000.00, 'https://paddy.vn/cdn/shop/files/ao-dai-tet-cho-cho-meo.png', 'Thương hiệu: Paddy\r\nPhù hợp cho: Chó/mèo mọi lứa tuổi\r\nÁo dài cho chó mèo là một trang phục đặc biệt dành riêng cho thú cưng của bạn trong dịp Tết. Đây là cách để chủ nuôi thể hiện tình yêu và sự quan tâm của bản thân đến chó mèo của mình. Đồ cho chó mèo này thường có họa tiết và hoa văn truyền thống như hoa mai, hoa đào hoặc các ký hiệu mang ý nghĩa tốt lành. Chúng thường được thiết kế với chất liệu vải nhẹ, thoáng mát và dễ chịu cho thú cưng. Áo dài có thể được sử dụng như cả áo cho chó và áo cho mèo, phù hợp với mọi lứa tuổi.', 'Lợi ích:\r\n\r\nGiúp thú cưng của bạn xinh đẹp hơn trong dịp Tết\r\nThành phần\r\n\r\nChất liệu: Vải lụa dày dặn in hình ảnh sắc nét, chất vải êm ái, thoáng mát\r\nCó nút bấm chắc chắn giúp tiện lợi hơn khi mặc\r\nQuần vải lụa đi kèm có thể tháo rời\r\nHướng dẫn sử dụng\r\n\r\nMặc trực tiếp cho thú cưng\r\nGỢI Ý CHỌN SIZE: Rộng ngực * dài lưng (Trọng lượng của bé)\r\n\r\n- S: 25cm*35cm (1.5~2.5kg)\r\n\r\n- M: 30cm*40cm (2.5~4kg)\r\n\r\n- L: 35cm*45cm (4~6kg)\r\n\r\n- XL: 40cm*50cm (6~8kg)\r\n\r\n*Với áo lẻ: tham khảo độ rộng vòng ngực\r\n\r\n**Với set áo kèm quần: tham khảo độ dài lưng\r\n\r\n***Lưu ý: Số liệu trên chỉ mang tính chất tương đối. Size áo còn phụ thuộc vào giống chó/mèo và độ dày lông. Để chọn chính xác cỡ áo, bạn có thể chat trực tiếp với Paddy để được tư vấn tốt nhất.\r\n\r\n#aochomeo #aochocho #aochomeo #poodle #meoanhlongngan #aotetchocho #aotetchomeo #thoitrangchomeo', 100, 1, 26, 3),
(6, 'Thức Ăn Hạt Trị Bệnh Cho Mèo Hỗ Trợ Chức Năng Thận Royal Canin Renal', 172000.00, 165000.00, 'https://paddy.vn/cdn/shop/files/thuc-an-cho-meo-royal-canin-renal-ho-tro-than.jpg', 'Thương hiệu: Royal Canin\r\nPhù hợp cho: Mèo (đang bị bệnh về thận)\r\nThức ăn cho mèo Royal Canin Renal là một loại thức ăn khô chuyên dụng dành cho mèo bị bệnh thận mãn tính. Nó được bào chế với các thành phần như protein chất lượng cao, phốt pho thấp, và chất chống oxy hóa, tất cả đều có lợi cho chức năng thận. Royal Canin Renal có thể được sử dụng cho tất cả các giống mèo, ở mọi lứa tuổi. Tuy nhiên, nó đặc biệt có lợi cho những con mèo bị bệnh thận mãn tính.', 'Lợi ích:\r\n\r\nHỗ trợ chức năng thận\r\nGiúp giảm nồng độ creatinin và urê trong máu\r\nGiúp ngăn ngừa các biến chứng của bệnh thận\r\nGiữ cho mèo khỏe mạnh và tràn đầy năng lượng\r\nThành phần\r\n\r\nBột thịt gà, protein động vật thủy phân, chất béo động vật, gạo, chất xơ thực vật, photpho, chất chống oxy hóa, chất xơ hòa tan, axit béo omega-3 và omega-6, vitamin và khoáng chất...\r\nHướng dẫn sử dụng\r\n\r\nChia làm 2-3 bữa/ngày\r\nChỉ định\r\n- Thận mạn tính kèm Ure huyết (IRIS* giai đoạn 2 đến giai đoạn 4).\r\n- Kiểm soát việc tái hình thành sỏi Canxi Oxalate ở mèo bị suy thận.\r\n- Kiềm hóa nước tiểu ngăn ngừa tái phát sỏi tiết niệu: sỏi urat và cystin.\r\nChống chỉ định: Mèo con/mèo mẹ đang mang thai và cho con bú.', 15, 1, 7, 8),
(7, 'Pate Cho Chó Sỏi Thận Royal Canin Urinary 85g', 45000.00, 38000.00, 'https://paddy.vn/cdn/shop/files/pate-cho-soi-than-royal-canin-urinary-85g_2.png', 'Thương hiệu: Royal Canin\r\nPhù hợp cho: Chó trưởng thành bị sỏi thận\r\nPate Cho Chó Royal Canin Urinary S/O là thức ăn dinh dưỡng hoàn chỉnh cho chó, được sản xuất bởi thương hiệu Royal Canin nổi tiếng thế giới. Với công thức đặc biệt, Royal Canin Urinary giúp hỗ trợ hòa tan sỏi Struvite và giảm tái phát nhờ các thành phần có thể axit hóa nước tiểu, ít chứa phân tử Magie (thành phần chính của sỏi Struvite).', 'Lợi ích:\r\n\r\nHỗ trợ hòa tan sỏi Struvite\r\nGiảm nguy cơ tái phát sỏi thận Struvite\r\nDuy trì cân bằng chất điện giải\r\nCung cấp đầy đủ dinh dưỡng cho chó trưởng thành\r\nThành phần\r\n\r\nThịt gà, gạo, gluten bắp, mỡ gà, protein động vật thủy phân, chất xơ thực vật, dầu cá, khoáng chất, men bia, chiết xuất men,...\r\nHướng dẫn sử dụng\r\n\r\nNên hỏi ý kiến của Bác Sĩ Thú Y trước khi cho ăn.\r\nCho chó sử dụng Urinary S/O từ 5 đến 12 tuần để hòa tan sỏi struvite và tối đa trong 6 tháng để giảm tái phát. Sau đó, chó cần được kiểm tra định kỳ tại Thú Y để nhận được lời khuyên cho chế độ ăn lâu dài.\r\nLưu ý: Cần kiểm tra chức năng thận của chó trước khi chuyển sang chế độ ăn Urinary S/O.Kết hợp pate cùng các sản phẩm dinh dưỡng khác.\r\nThường xuyên vệ sinh dụng cụ ăn uống của chó.\r\nBảo quản nơi thoáng mát\r\n👉Xem thêm các sản phẩm khác tại Paddy.vn\r\n\r\n#patechomeo #patechocho #patechochocon #patehop #patecho #doancho #doanchocho #patechochopoodle #pateroyalcanin #royalcanin #royalcaninchocho', 23, 1, 90, 7),
(8, 'Thức Ăn Hạt Điều Trị Bệnh Cho Mèo Hỗ Trợ Tiêu Hoá Royal Canin Gastrointestinal\r\n', 170000.00, 165000.00, 'https://paddy.vn/cdn/shop/files/RoyalCaninMeoGastrointestinal.jpg', 'Hạt Royal Canin #Gastrointestinal Hỗ Trợ Mèo Có Vấn Đề Về Tiêu Hóa\r\n\r\nThực phẩm chức năng Royal Canin Gastrointestinal hỗ trợ mèo mắc phải các vấn đề về tiêu hóa.\r\n\r\nSử dụng sản phẩm khi có chỉ định của Bác Sĩ Thú Y / Đọc kỹ hướng dẫn trước khi sử dụng.', 'Hạt Royal Canin #Gastrointestinal Hỗ Trợ Mèo Có Vấn Đề Về Tiêu Hóa\r\n\r\nThực phẩm chức năng Royal Canin Gastrointestinal hỗ trợ mèo mắc phải các vấn đề về tiêu hóa.\r\n\r\nSử dụng sản phẩm khi có chỉ định của Bác Sĩ Thú Y / Đọc kỹ hướng dẫn trước khi sử dụng.\r\n\r\nKHUYẾN CÁO\r\nTrong trường hợp mèo mắc chứng đường ruột cấp tính, để cải thiện đường ruột yêu cầu phải kiểm soát dinh dưỡng ít nhất 3 tuần. Đối với những chú mèo mắc chứng mãn tính nên duy trì sử dụng chế độ ăn Royal Canin Gastrointestinal suốt đời.\r\n\r\nKhẩu phần ăn trong bảng hướng dẫn bên dưới nên chia thành nhiều bữa nhỏ trong ngày. Sản phẩm này có thể dụng cho mèo con.\r\n\r\nSử dụng kết hợp với Thức ăn ướt Royal Canin Gastrointestinal để đạt được kết quả tốt hơn.', 100, 1, 98, 8),
(9, 'Pate Mèo Trưởng Thành Nutri Plan Chăm Sóc Sức Khỏe\r\n', 50000.00, 40000.00, 'https://paddy.vn/cdn/shop/files/pate-meo-truong-thanh-nutri-plan-health-lon-160g.png', 'Pate Mèo Trưởng Thành Nutri Plan Chăm Sóc Sức Khỏe\r\n\r\nThông tin sản phẩm Pate chức năng Nutri Plan\r\nĐối tượng sử dụng: Mèo trưởng thành\r\n\r\nSản xuất tại: Dongwon, 68 Mabang-ro, Seocho-gu, Seoul, Hàn Quốc\r\n\r\nChức năng: Là sản phẩm thức ăn ướt đóng hộp được Dongwon trực tiếp sản xuất nhằm đem lại giải pháp dinh dưỡng chuyên biệt và ưu việt cho sức khỏe của mèo.', 'Pate chức năng Nutri Plan da lông khoẻ đẹp bóng mượt\r\n– Chất béo Omega-3 có trong cá ngừ cùng Vitamin và khoáng chất Dongwon trực tiếp sản xuất tốt cho sức khoẻ da và lông.\r\n– Chứa Inulin tốt cho đường ruột và nhuận tràng.\r\n– 100% Nguyên liệu từ cá ngừ Dongwon giàu dinh dưỡng.\r\n– Hỗn hợp Vitamin và khoáng chất Dongwon Giúp cải thiện sức khoẻ da và lông.\r\n– Inulin Canxi Tốt cho sức khỏe đường ruột.\r\nPate chức năng Nutri Plan đường ruột & tiết niệu\r\n– Chứa Inulin, men tiêu hoá, chiết xuất Yucca tốt cho đường ruột và nhuận tràng.\r\n– Chứa chiết xuất quả nam việt quất tốt cho cơ quan tiết niệu.\r\n– 100% Nguyên liệu từ cá ngừ Dongwon giàu dinh dưỡng.\r\n– Inulin và chiết xuất Yucca Tốt cho sức khỏe đường ruột, nhuận tràng.\r\nPate chức năng Nutri Plan kiểm soát cân nặng & xương khớp\r\n- Chứa chất xơ, đường Maltodextrin và chiết xuất hạt cỏ sữa giúp hỗ trợ kiểm soát cân nặng\r\n- Chứa Glucosamin, bột sụn cá mập tốt cho xương khớp\r\n- 100% Nguyên liệu từ cá ngừ Dongwon giàu dinh dưỡng.\r\nPate chức năng Nutri Plan miễn dịch & tiết niệu\r\n- Saponin và Vitamin E có trong hồng sâm tốt cho hệ miễn dịch.\r\n- Chứa chiết xuất quả nam việt quất tốt cho cơ quan tiết niệu.\r\n- 100% Nguyên liệu từ cá ngừ Dongwon giàu dinh dưỡng.\r\n- Cá ngừ thịt đỏ Chứa hàm lượng lớn axit amin cần thiết cho mèo.\r\n- Chiết xuất hồng sâm Chứa Saponin rất tốt cho hệ miễn dịch.', 200, 1, 123, 8),
(10, 'Pate Mèo Nutri Plan Cá Ngừ Mix (Lon 160g)\r\n', 50000.00, 32000.00, 'https://paddy.vn/cdn/shop/files/pate-meo-nutri-plan-lon-160g_1.png', 'Thương hiệu: Nutri Plan\r\nPhù hợp cho: Mèo mọi lứa tuổi\r\nPate mèo Nutri Plan Cá Ngừ Mix là sản phẩm thức ăn ướt dành cho mèo, được sản xuất bởi công ty Nutri Plan Hàn Quốc. Sản phẩm có thành phần chính là cá ngừ, kết hợp với các loại nguyên liệu khác như cá hồi, cá cơm, gà, cua,... cung cấp đầy đủ các chất dinh dưỡng cần thiết cho mèo, giúp mèo khỏe mạnh và phát triển toàn diện.', 'Lợi ích:\r\n\r\nCung cấp đầy đủ các chất dinh dưỡng cần thiết cho mèo, bao gồm protein, chất béo, carbohydrate, vitamin và khoáng chất.\r\nHỗ trợ hệ tiêu hóa khỏe mạnh, giúp mèo dễ dàng hấp thu dinh dưỡng.\r\nTăng cường sức đề kháng, giúp mèo khỏe mạnh và phòng ngừa bệnh tật.\r\nTăng cường hương vị, kích thích sự thèm ăn của mèo.\r\nThành phần\r\n\r\nCá ngừ giàu protein và các chất dinh dưỡng như DHA, Omega 3, EPA…. Với chiết xuất trà xanh giúp giảm mùi hôi ở miệng và cả mùi phân khi mèo đi vệ sinh, giúp kích thích mèo ăn ngon miệng hơn.\r\nHướng dẫn sử dụng\r\n\r\nCó thể cho ăn trực tiếp và chỉ cần bổ sung thêm nước là có thể duy trì sức khoẻ bình thường của thú cưng\r\nBảo quản nhiệt độ phòng, để ngăn mát và dùng hết trong 48 tiếng sau khi mở nắp', 23, 1, 21, 5),
(11, 'Pate Mèo Royal Canin Intense Beauty Chăm Sóc Da Lông 85g\r\n', 39000.00, 27000.00, 'https://paddy.vn/cdn/shop/files/pate-royal-canin-intense-beauty-jelly-85g-paddy-2_3bc48faa-621a-4a8c-be10-b25ebc155f85.jpg', 'Thương hiệu: Royal Canin\r\nPhù hợp cho: Mèo trưởng thành (trên 12 tháng tuổi)\r\nPate mèo Royal Canin Intense Beauty chăm sóc da lông là sản phẩm được thiết kế dành riêng cho mèo trên 12 tháng tuổi. Pate Royal Canin Intense Beauty chứa các thành phần trích xuất từ thịt, sữa và các khoáng chất thiết yếu nhằm hỗ trợ tăng cường hệ thống miễn dịch cho mèo, duy trì cân nặng và đặc biệt là chăm sóc da lông khỏe đẹp. Thức ăn cho mèo ROYAL CANIN được tạo ra với sự cân bằng tối ưu giữa các protein, chất béo và carbohydrate giúp mèo cảm thấy ngon miệng mà vẫn bổ sung dinh dưỡng hoàn hảo.', 'Lợi ích:\r\n\r\nTạo ra nguồn dinh dưỡng phù hợp tối ưu theo bản năng ưa thích của mèo trưởng thành, giúp mèo ăn ngon miệng hơn.\r\n\r\nHỗ trợ tăng cường hệ bài tiết khỏe mạnh.\r\n\r\nHàm lượng chất béo phù hợp để kiểm soát trọng lượng hiệu quả, giữ cân nặng lí tưởng cho mèo.\r\n\r\nBao gồm các chất dinh dưỡng cụ thể để nuôi dưỡng và duy trì độ bóng mượt của lông.\r\nThành phần dinh dưỡng\r\n\r\nThành phần: Thịt và các dẫn xuất động vật, cá và các chất dẫn xuất cá, ngũ cốc, chiết xuất protein thực vật, dầu và chất béo, các chất dẫn xuất nguồn gốc thực vật, khoáng chất, đường khác nhau.\r\nPhụ gia dinh dưỡng: Vitamin D3: 95 IU, E1 (Sắt): 10 mg, E2 (lốt): 0,34 mg, E4 (Đồng): 2,7 mg, E5 (Mangan): 3 mg, E6 (Kẽm) ): 30 mg.\r\nHướng dẫn sử dụng\r\n\r\nDựa theo độ tuổi và cân nặng của mèo, cho ăn một lượng vừa đủ vào các giờ cố định.\r\nCó thể trộn với các thức ăn khác hoặc cho ăn trực tiếp.\r\nBảo quản: bảo quản ở nơi cao ráo, thoáng mát, tránh ánh nắng mặt trời. Khi mèo dùng không hết, các bạn bọc kín pate và bảo quản tủ lạnh tối đa 3 ngày và nhớ để ở nhiệt độ phòng cho pate bớt lạnh trước khi cho mèo dùng tiếp.\r\nLưu ý:\r\n\r\nKhông cho mèo ăn quá nhiều trong một lần.\r\nNên cho mèo ăn thức ăn chế biến riêng, không cho ăn thức ăn thừa của người. Vì thức ăn của người có nhiều thành phần khiến mèo bị rối loạn tiêu hóa, ảnh hưởng đến sức khỏe của mèo.', 29, 1, 12, 8),
(12, 'Pate Cho Chó Monge Dog Vet Solution Chăm Sóc Sức Khỏe 150g\r\n', 45000.00, 38000.00, 'https://paddy.vn/cdn/shop/files/pate-cho-cho-monge-vet-solution-dog.png', 'Thương hiệu: Monge\r\nPhù hợp cho: Chó mọi lứa tuổi (đang cần Chăm sóc tiết niệu, Tăng cường miễn dịch, Chăm sóc tóc và da, Hỗ trợ tiêu hóa)\r\nPate Cho Chó Monge Dog Vet Solution là sản phẩm dinh dưỡng dành cho chó được sản xuất bởi Monge, một thương hiệu thức ăn cho thú cưng nổi tiếng của Ý. Sản phẩm được chế biến từ các nguyên liệu tươi ngon, chất lượng cao, bao gồm thịt, cá, rau củ và các loại vitamin và khoáng chất cần thiết cho sự phát triển và sức khỏe của chó. Pate Monge có nhiều hương vị khác nhau cho bạn lựa chọn, phù hợp với khẩu vị của từng chú chó. Sản phẩm có hương vị thơm ngon, hấp dẫn, giúp chó ăn ngon miệng và hấp thu dinh dưỡng tốt hơn. Pate Monge Dog Vet Solution còn có tác dụng hỗ trợ hệ tiêu hóa, tăng cường hệ miễn dịch, giúp chó khỏe mạnh và năng động hơn.', 'Lợi ích:\r\n\r\nMONGE VETSOLUTION DOG DERMATOSIS - Hỗ trợ chức năng da trong trường hợp bệnh da liễu và rụng lông quá nhiều.\r\nMONGE VETSOLUTION DOG GASTROINTESTINAL - Chứa ít chất béo, được bào chế để giảm rối loạn hấp thu đường ruột và bù đắp cho tình trạng tiêu hóa kém.\r\nMONGE VETSOLUTION RENAL and OXALATE - Hỗ trợ chức năng thận trong trường hợp suy thận mãn tính hoặc tạm thời. Nó cũng được bào chế để giảm sự hình thành sỏi Oxalat.\r\nMONGE VETSOLUTION RECOVERY - Thúc đẩy quá trình phục hồi sức khỏe trong thời gian dưỡng bệnh. \r\nThành phần dinh dưỡng\r\n\r\nDermatosis: Chứa Nucleotides hỗ trợ hệ miễn dịch, Lô hội hỗ trợ làn da, Xylooligosaccharides (XOS) để bảo vệ hệ vi sinh vật đường ruột. Công thức này được đặc trưng bởi mức độ cao của axit linoleic (LA) và tổng axit eicosapentaeonic (EPA) và axit docosahexaeonic (DHA)\r\nGastrointestinal: Chứa Nucleotides để hỗ trợ hệ thống miễn dịch, Hạt dẻ ngựa hỗ trợ đường ruột, Xylo-oligosaccharides (XOS) để đảm bảo hệ vi sinh vật đường ruột. Thức ăn dễ tiêu hóa với tăng natri và kali.\r\nRecovery: Chứa Nucleotides để hỗ trợ hệ thống miễn dịch, hạt cây kế để hỗ trợ dưỡng bệnh, Xylo-oligosaccharides (XOS) để bảo vệ hệ vi sinh vật đường ruột. Công thức được đặc trưng bởi mật độ năng lượng cao, nồng độ cao của các chất dinh dưỡng thiết yếu và các thành phần dễ tiêu hóa.\r\nRenal và Oxalat: Chứa Nucleotides để hỗ trợ hệ thống miễn dịch, Trà xanh để hỗ trợ thận, Xylo-oligosaccharides (XOS) để đảm bảo hệ vi sinh vật đường ruột. Công thức này được đặc trưng bởi mức độ protein thấp nhưng chất lượng cao và mức độ hạn chế của phốt pho, mức độ canxi thấp, mức độ vitamin D thấp và đặc tính kiềm hóa nước tiểu.\r\nHướng dẫn sử dụng\r\n\r\nCho ăn trực tiếp hoặc trộn với thức ăn.\r\nNên hỏi ý kiến ​​của bác sĩ thú y trước khi sử dụng.\r\nCho ăn 600g mỗi ngày cho chó trưởng thành 10kg.\r\nThời gian sử dụng khuyến nghị tối đa 2 tháng.', 30, 1, 21, 7),
(13, 'Pate Cho Chó Sốt Viên Plaisir Nhiều Vị\r\n', 38000.00, 29000.00, 'https://paddy.vn/cdn/shop/files/pate-cho-cho-sot-vien-plaisir-nhieu-vi.png', 'Dòng pate cho CHÓ của PLAISIR gồm có:\r\n\r\n- Chó con thịt Gà (hộp 300gr)\r\n\r\n- Chó con thịt Gà (gói 100gr)\r\n\r\n- Chó lớn thịt Bò (hộp 300gr)\r\n\r\n- Chó lớn thịt Gà (hộp 300gr)\r\n\r\n- Chó lớn Gà tây (hộp 300gr)\r\n\r\n- Chó lớn Gà rau (gói 100gr)\r\n\r\n- Chó lớn Bò (gói 100gr)', 'Les Repas PLAISIR là thương hiệu thức ăn thú cưng cao cấp đến từ Pháp. Với\r\n\r\nPâté và thịt băm của PLAISIR được sản xuất tại các cơ sở sản xuất ở Vire, nằm ở trung tâm của vùng Normandy nước Pháp, sử dụng các nguyên liệu chất lượng được tìm kiếm từ các đối tác đáng tin cậy, hầu hết đều nằm ở Pháp. Mối liên kết này là điều thúc đẩy cam kết chất lượng của PLAISIR, không chỉ đối với khách hàng mà còn đối với các giống thú cưng ở mọi giai đoạn phát triển.\r\n\r\nVị và cảm giác là các tiêu chí kết hợp ưu tiên trong các công thức của Repas Plaisir! Các sản phẩm của PLAISIR rất hấp dẫn các boss vì được kết hợp nhiều hương vị tinh tế với kết cấu mềm, thơm ngon.', 27, 1, 2, 4),
(14, 'Pate Cho Chó Wanpy Premium Vị Gà 80g\r\n', 20000.00, 13000.00, 'https://paddy.vn/cdn/shop/files/pate-cho-cho-wanpy-premium-vi-ga-80g.png', 'Lợi ích:\r\n\r\nĐược làm từ thịt gà thật, gan gà và các nguyên liệu khác\r\nHương vị gà mà hầu hết chó đều thích\r\nLà nguồn protein và chất dinh dưỡng tốt\r\nChứa vitamin và khoáng chất thiết yếu', 'Thương hiệu: Wanpy\r\nPhù hợp cho: Chó mọi lứa tuổi\r\nPate Cho Chó Wanpy Premium Vị Gà là một loại thức ăn ướt dành cho chó được làm từ thịt gà thật, gan gà và các thành phần khác. Pate cung cấp một nguồn protein và chất dinh dưỡng tốt cho chó. Nó cũng chứa các vitamin và khoáng chất thiết yếu giúp chó khỏe mạnh và vui vẻ.\r\nLợi ích:\r\n\r\nĐược làm từ thịt gà thật, gan gà và các nguyên liệu khác\r\nHương vị gà mà hầu hết chó đều thích\r\nLà nguồn protein và chất dinh dưỡng tốt\r\nChứa vitamin và khoáng chất thiết yếu\r\nThành phần dinh dưỡng\r\n\r\nThịt gà, gan gà, thịt vịt, rau củ,...\r\nHướng dẫn sử dụng\r\n\r\nPate có thể được cho chó ăn trực tiếp hoặc trộn với thức ăn khô.\r\nPate nên được bảo quản trong tủ lạnh sau khi mở.\r\nKhông cho chó ăn pate quá hạn sử dụng.', 46, 1, 20, 4),
(15, 'Pate TƯƠI The Pet Cho Chó Mèo Biếng Ăn (1kg) - Ship Now/Grab 2H\r\n', 120000.00, 105000.00, 'https://paddy.vn/cdn/shop/files/123.jpg', 'CHẤP HẾT TẤT CẢ MÈO BIẾNG ĂN, KHÓ ĂN, KÉN MỌI LOẠI THỨC ĂN.\r\n💯 100% nguyên liệu tự nhiên, không độn rau củ, chứa độ ẩm & đạm tự nhiên cao từ 60-84%.\r\n💯 Năng lượng cao hơn vượt trội so với các dòng sản phẩm khác trên thị trường (trung bình ở mức 400kcal/kg).\r\n💯 Công thức siêu cấp nước, giúp ngăn ngừa sỏi thận.\r\n💯 Với giá chỉ từ 8k/bữa ăn là Boss đã có được bữa ăn thơm ngon, tốt cho sức khỏe.\r\n💯 Chỉ cần bảo quản sản phẩm trong ngăn mát, không cần chế biến hay hâm nóng.', 'Pate Tươi Cho Mèo Hỗn Hợp cho Chó Mèo Biếng Ăn được làm từ hỗn hợp cá biển và gan gà tươi nguyên chất thích hợp dùng cho Chó Mèo.\r\n\r\nCHẤP HẾT TẤT CẢ MÈO BIẾNG ĂN, KHÓ ĂN, KÉN MỌI LOẠI THỨC ĂN.\r\n💯 100% nguyên liệu tự nhiên, không độn rau củ, chứa độ ẩm & đạm tự nhiên cao từ 60-84%.\r\n💯 Năng lượng cao hơn vượt trội so với các dòng sản phẩm khác trên thị trường (trung bình ở mức 400kcal/kg).\r\n💯 Công thức siêu cấp nước, giúp ngăn ngừa sỏi thận.\r\n💯 Với giá chỉ từ 8k/bữa ăn là Boss đã có được bữa ăn thơm ngon, tốt cho sức khỏe.\r\n💯 Chỉ cần bảo quản sản phẩm trong ngăn mát, không cần chế biến hay hâm nóng.\r\n\r\nPaddy có sẵn có 2 mùi vị thơm ngon #BestSeller, hấp dẫn các bé kén ăn\r\n✅ Hỗn Hợp Gà - cho Chó & Mèo\r\n✅ Hỗn Hợp Cá - cho Mèo\r\n✅ Hỗn Hợp Gà - cho Chó & Mèo\r\n\r\nKhối lượng: hộp to 1kg', 43, 1, 90, 4),
(16, 'Pate Cho Chó Mèo Mọi Lứa Tuổi Thịt Tươi Kings Pet (Lon 380g)\r\n', 45000.00, 40000.00, 'https://paddy.vn/cdn/shop/files/Social_1.jpg', '– Không chất bảo quản, không màu, mùi nhân tạo.\r\n\r\n– Có thể cho ăn trực tiếp và chỉ cần bổ sung thêm nước là có thể duy trì sức khoẻ bình thường của mèo yêu. – Hương vị thơm ngon, kết cấu đặc mịn.\r\n\r\n– Nguyên liệu hoàn toàn tự nhiên được lựa chọn cẩn thận, không trộn tinh bột.\r\n\r\n– Không độn rau củ.\r\n\r\n– Siêu cấp nước cho hệ tiêu hoá, hỗ trợ hydrat hóa tối ưu. Cung cấp đầy đủ Vitamin A, E, nhóm B, canxi và khoáng chất cho mèo.\r\n\r\n– Phù hợp cho mèo từ 01 tháng tuổi, ở mọi giai đoạn sức khoẻ.\r\n\r\n– Nhuận trường, thức ăn giúp chó mèo dễ dàng đi vệ sinh do có bổ sung Xanthan gum.', 'Pate Lon King\'s Pet Cho Chó Mèo 380gr\r\n\r\n1. Pate Hỗn hợp gà King’s Pet lon 380g – thức ăn cho chó mèo từ 01 tháng tuổi. Được làm từ thịt gà đã được kiểm dịch chặt chẽ, kết hợp cùng gan gà tươi mọng được thanh trùng ở nhiệt độ cao nhằm giữ nguyên hương vị thơm ngon và hàm lượng dinh dưỡng.\r\n\r\n2. Pate Hỗn hợp cá King’s Pet lon 380g – thức ăn cho mèo từ 01 tháng tuổi. Được làm từ cá nục tươi đánh bắt ở vùng biển miền Nam Việt Nam cùng với gan gà tươi nguyên chất được thanh trùng ở nhiệt độ cao nhằm giữ trọn hương vị thơm ngon và các chất dinh dưỡng.\r\n\r\n3. Pate Cá hồi King’s Pet lon 380g – thức ăn cho mèo từ 01 tháng tuổi Được làm từ cá hồi tươi nguyên chất được thanh trùng ở nhiệt độ cao nhằm giữ trọn hương vị thơm ngon và các chất dinh dưỡng.\r\n\r\n4. Giò Heo Hầm Nước Dùng King’s Pet Lon 380g - thức ăn cho chó từ 01 tháng tuổi. Sử dụng nguồn thịt giò heo tươi ngon xuất xứ hoàn toàn Việt Nam, không sử dụng chất độn, chất tạo dày. Kết cấu hoàn toàn từ nguyên liệu thật. ƯU ĐIỂM CỦA PATE KING\'S PET\r\n\r\n– Không chất bảo quản, không màu, mùi nhân tạo.\r\n\r\n– Có thể cho ăn trực tiếp và chỉ cần bổ sung thêm nước là có thể duy trì sức khoẻ bình thường của mèo yêu. – Hương vị thơm ngon, kết cấu đặc mịn.\r\n\r\n– Nguyên liệu hoàn toàn tự nhiên được lựa chọn cẩn thận, không trộn tinh bột.\r\n\r\n– Không độn rau củ.\r\n\r\n– Siêu cấp nước cho hệ tiêu hoá, hỗ trợ hydrat hóa tối ưu. Cung cấp đầy đủ Vitamin A, E, nhóm B, canxi và khoáng chất cho mèo.\r\n\r\n– Phù hợp cho mèo từ 01 tháng tuổi, ở mọi giai đoạn sức khoẻ.\r\n\r\n– Nhuận trường, thức ăn giúp chó mèo dễ dàng đi vệ sinh do có bổ sung Xanthan gum.\r\n\r\nHDSD Sản phẩm được có thể bảo quản 03 năm ở nơi khô ráo, thoáng mát, tránh ánh nắng trực tiếp. Sau khi khui hộp có thể bảo quản tối đa 05 ngày trong ngăn mát tủ lạnh. Sản phẩm có bổ sung nắp nhựa chống khô tiện dụng.', 98, 1, 130, 5),
(17, 'Thức Ăn Hạt Mềm Chó ANF Soft\r\n', 58000.00, 55000.00, 'https://paddy.vn/cdn/shop/files/ANF-Soft.jpg', 'Hạt Mềm Cho Chó ANF Soft (Hàn Quốc)\r\n\r\nBộ đôi sản phẩm thức ăn hạt mềm ANF SOFT với 2 hương vị Gà và Cá hồi thơm ngon sẽ giúp Sen có thêm lựa chọn nữa để da dạng bữa ăn cho cún cưng nhà mình.\r\n\r\n#ANFSOFT là sự kết hợp giữa thịt chất lượng cao, rau củ, trái cây tươi và các lợi khuẩn, bổ sung các dưỡng chất thiết yếu giúp hỗ trợ tiêu hóa, tăng cường sức khỏe cho cún.\r\n\r\nThức ăn hạt mềm ANF SOFT là một lựa chọn phù hợp dành cho các chú chó trong mọi giai đoạn phát triển, đặc biệt là chó con, chó già có tình trạng kén ăn hoặc răng yếu.', 'Hạt Mềm Cho Chó ANF Soft (Hàn Quốc)\r\n\r\nBộ đôi sản phẩm thức ăn hạt mềm ANF SOFT với 2 hương vị Gà và Cá hồi thơm ngon sẽ giúp Sen có thêm lựa chọn nữa để da dạng bữa ăn cho cún cưng nhà mình.\r\n\r\n#ANFSOFT là sự kết hợp giữa thịt chất lượng cao, rau củ, trái cây tươi và các lợi khuẩn, bổ sung các dưỡng chất thiết yếu giúp hỗ trợ tiêu hóa, tăng cường sức khỏe cho cún.\r\n\r\nThức ăn hạt mềm ANF SOFT là một lựa chọn phù hợp dành cho các chú chó trong mọi giai đoạn phát triển, đặc biệt là chó con, chó già có tình trạng kén ăn hoặc răng yếu.', 200, 1, 5, 4),
(18, 'Pate Cho Chó Thịt & Rau Củ Nutri Plan Holic (Lon 85g)\r\n', 48000.00, 30000.00, 'https://paddy.vn/cdn/shop/files/pate-cho-nutri-plan-holic-85g-2-1691471632544.jpg', 'Lợi ích:\r\n\r\nChứa nhiều chất xơ, carotenoid, vitamin tốt cho đường ruột và hệ tiêu hóa.\r\nChứa nhiều beta-carotene tốt cho mắt. \r\nChứa các dưỡng chất như vitamin, axit folic, magie, canxi.\r\nChứa axit amin lysine cần thiết cho cơ thể và vitamin A, B, K.\r\nChứa polyphenol hỗ trợ tác dụng oxy hóa.\r\nTốt cho sức khỏe đường tiết niệu (đường tiểu).', 'Thương hiệu: Nutri Plan\r\nPhù hợp cho: Chó mọi lứa tuổi\r\nPate Cho Chó Nutri Plan Holic được sản xuất tại Hàn Quốc đảm bảo về chất lượng đạt tiêu chuẩn an toàn thực phẩm, nguyên liệu tin cậy. Pate Holic có chứa 10 loại rau củ quả tốt cho sức khỏe do viện nghiên cứu khoa học thực phẩm Dongwon F&B tuyển chọn, bao gồm: Nam việt quất, súp lơ, cà rốt, đậu Hà Lan, táo, lê, cần tây, bắp cải, khoai tây và bí đỏ. Pate cho chó là món ăn không thể thiếu giúp duy trì sự cân bằng và hấp thụ các dưỡng chất cần thiết như vitamin, chất xơ,…\r\nLợi ích:\r\n\r\nChứa nhiều chất xơ, carotenoid, vitamin tốt cho đường ruột và hệ tiêu hóa.\r\nChứa nhiều beta-carotene tốt cho mắt. \r\nChứa các dưỡng chất như vitamin, axit folic, magie, canxi.\r\nChứa axit amin lysine cần thiết cho cơ thể và vitamin A, B, K.\r\nChứa polyphenol hỗ trợ tác dụng oxy hóa.\r\nTốt cho sức khỏe đường tiết niệu (đường tiểu).\r\nThành phần dinh dưỡng\r\n\r\nThành phần: Cá ngừ Dongwon/ức gà tươi, 10 loại rau củ quả tốt cho sức khỏe, nước dùng cô đặc vị gà thơm ngon, nước cốt cá minh thái giàu dưỡng chất, 200 triệu lợi khuẩn (probiotics), Axit amin làm tăng hương vị\r\nHướng dẫn sử dụng\r\n\r\nĐối tượng khuyên dùng:\r\nCún cần đồ ăn bổ sung tốt cho sức khỏe\r\nCún cần bổ sung vitamin và chất xơ\r\nCún thích thịt nạc\r\nCún thích pate đậm đà\r\nCún kén ăn\r\nLưu ý khi sử dụng: Có thể có vảy hoặc xương cá ngừ, cuống hoặc vỏ rau củ do đặc tính sản phẩm. Cẩn thận khi mở nắp, bảo quản và vứt bỏ vì phần nắp hộp có cạnh sắc nhọn.\r\nĐổ ra bát rồi cho cún ăn.\r\nTránh ánh sáng trực tiếp và vi khuẩn có hại. Bảo quản ở nơi thoáng mát và không bị ẩm mốc.\r\nBảo quản lạnh hoặc sử dụng nhanh sau khi mở nắp để tránh làm giảm chất lượng sản phẩm.\r\nĐọc kỹ lưu ý trên nắp hộp khi mở.', 49, 1, 8, 4),
(19, 'Máy Ăn Tự Động Petkit MINI 2.85L - Bát đựng inox (BH 12T)\r\n', 2600000.00, 2499000.00, 'https://paddy.vn/cdn/shop/files/may-an-tu-dong-petkit-mini-3l-bat-inox-bh-12t.png', 'Công Dụng\r\n\r\nMáy ăn tự động Petkit hoạt động dựa trên sự điều chỉnh của app Petkit trên smartphone. Bạn hoàn toàn yên tâm đi công tác xa khi máy được cài đặt phân phối khẩu phần ăn hàng ngày cho thú cưng của mình.\r\n\r\nChi tiết kỹ thuật\r\n\r\n- Màu sắc: trắng\r\n- Kích thước: ~ 40 x 25 x 25 (cm)\r\n- Dung tích: 3l', 'Bạn luôn lo lắng khi mình đi du lịch, đi công tác thì vấn đề ăn uống của pet sẽ như thế nào? Và máy ăn tự động Petkit sẽ giúp bạn giải quyết triệt để vấn đề đó\r\n\r\nMáy cho ăn tự động hỗ trợ\r\n\r\n - Kết nối với ứng dụng Petkit trên smartphone và thông qua Wifi để điều chỉnh, cài đặt bữa ăn hàng ngày cho pet\r\n\r\n- Lập trình tối đa ~10 bữa/ngày\r\n\r\n- Hộp hút ẩm bên trong máng ăn giữ cho thực phẩm tươi. Bạn có thể sử dụng toàn bộ công suất của bộ nạp này mà không lo thức ăn bị ôi thiu hay ẩm mốc. Điều này có nghĩa là nó tồn tại trong một thời gian dài mà không cần nạp thêm.\r\n\r\n- Dung tích máy chứa được đến 3 lít thực phẩm khô, chấp nhận tất cả các kích thước hình dạng của thức ăn, thậm chí là hình vuông, thích hợp cho mọi thú cưng - Khay đựng thức ăn có thể tháo rời, dễ dàng vệ sinh', 57, 1, 40, 2),
(20, 'Thẻ Tên Chó Mèo Thiết Kế Theo Yêu Cầu\r\n', 99000.00, 90000.00, 'https://paddy.vn/cdn/shop/products/1_00d17c89-f7c0-4910-b60b-c52986852013.jpg', 'Thông tin sản phẩm:\r\n- Chất liệu thẻ tên inox bền bỉ\r\n- Được tư vấn thiết kế theo yêu cầu\r\n- Được khắc laser 2 mặt, không bị bong tróc, phai mờ\r\n- Kiểu cách đa dạng , đủ các mẫu trên thị trường\r\n- Nhận đặt số lượng lớn giá ưu đãi', 'Thẻ Khắc Tên Cho Chó Mèo Thiết Kế Theo Yêu Cầu\r\n\r\nThẻ tên không chỉ là sản phẩm làm đẹp cho thú cưng, mà còn là 1 vật dụng hữu ích giúp Sen tìm lại Boss trong trường hợp thất lạc, cơ hội mang bé về với bạn sẽ cao hơn rất nhiều chỉ với 1 chiếc thẻ tên!!!\r\n\r\nThông tin sản phẩm:\r\n- Chất liệu thẻ tên inox bền bỉ\r\n- Được tư vấn thiết kế theo yêu cầu\r\n- Được khắc laser 2 mặt, không bị bong tróc, phai mờ\r\n- Kiểu cách đa dạng , đủ các mẫu trên thị trường\r\n- Nhận đặt số lượng lớn giá ưu đãi\r\n\r\n**HƯỚNG DẪN MUA HÀNG**\r\nCHAT VỚI SHOP ĐỂ ĐƯỢC TƯ VẤN THIẾT KẾ MIỄN PHÍ\r\n👉 Bước 1: Chọn Mã số thẻ\r\n👉 Bước 2: Điền thông tin trên thẻ:\r\nMặt trước: tên, ngày sinh...\r\nMặt sau: số điện thoại, lời nhắn..\r\n👉 Bước 3: Chọn icon, sticker trang trí theo ý thích\r\n\r\nThời gian giao hàng: Tp.HCM (2-3 ngày), các tỉnh thành khác (3-7 ngày)', 13, 1, 103, 1),
(21, 'Balo Vải Vận Chuyển Chó Mèo Big Space 25x34x37cm\r\n', 400000.00, 350000.00, 'https://paddy.vn/cdn/shop/files/balo-van-chuyen-cho-meo-big-space-25x34x37cm.png', 'Cách vệ sinh và bảo quản:\r\n\r\n- Khi chó hoặc mèo của bạn tè hay đi bậy vào balo thì trước hết bạn nên dùng khăn giấy lau bớt đi.\r\n\r\n- Sau đó rửa nước và ngâm trong xà bông giặt đồ hoặc chất tẩy quần áo.\r\n\r\n- Ngâm 20-30 phút, sau đó chà rửa sạch xà bông.\r\n\r\n- Phơi khô nơi có nhiều nắng để mau khô và khử khuẩn balo.', 'Balo Vải Vận Chuyển Chó Mèo Big Space 25x34x37cm\r\n\r\n \r\n\r\nMÀU SẮC: GIAO NGẪU NHIÊN\r\n\r\n \r\n\r\nPhù Hợp Cân Nặng <9KG\r\n\r\n \r\n\r\nThông tin sản phẩm: \r\n\r\n- Balo chất lượng đảm bảo: độ thông gió cần thiết, an toàn và không gian rộng rãi với kích thước lớn có thể đựng được nhiều pet và pet nặng dưới 9kg.\r\n\r\n- Thiết kế phong cách phối màu đáng yêu\r\n\r\n- Balo có tấm cửa phía bên hông, dễ dàng cho thú cưng ra vào mà không cần mở hết balo.\r\n\r\n- Nhiều lưới thoáng khí hai bên và trên trần balo giúp thú cưng không ngộp.\r\n\r\n- Quai đeo chắc chắn và tỉ mỉ có đệm giúp sen không bị đau vai.\r\n\r\n- Khóa kéo trượt êm ái.\r\n\r\n- Kích thước: Cao 34cm x Ngang 37cm x Rộng 25cm\r\n\r\n \r\n\r\nCách vệ sinh và bảo quản:\r\n\r\n- Khi chó hoặc mèo của bạn tè hay đi bậy vào balo thì trước hết bạn nên dùng khăn giấy lau bớt đi.\r\n\r\n- Sau đó rửa nước và ngâm trong xà bông giặt đồ hoặc chất tẩy quần áo.\r\n\r\n- Ngâm 20-30 phút, sau đó chà rửa sạch xà bông.\r\n\r\n- Phơi khô nơi có nhiều nắng để mau khô và khử khuẩn balo.\r\n\r\n \r\n\r\n#Balo\r\n\r\n#balo\r\n\r\n#BALOCHOTHÚCƯNG\r\n\r\n#BALO_CHO_THÚ_CƯNG\r\n\r\n#balochothucung\r\n\r\n#balo_cho_thu_cung\r\n\r\n#balovanchuyenchomeo\r\n\r\n#balovậnchuyểnchómèo\r\n\r\n#balo_van_chuyen_cho_meo\r\n\r\n#balochốngnướcchochómèo\r\n\r\n#balochongnuocchochomeo\r\n\r\n#balo_lo_chong_nuoc_cho_cho_meo\r\n\r\n#balochởchómèokíchthướclớn\r\n\r\n#balochởthúcưngkíchthướclớn', 10, 1, 24, 9),
(22, 'Túi Vận Chuyển Chó Mèo Chất Liệu Lưới\r\n', 200000.00, 160000.00, 'https://paddy.vn/cdn/shop/files/b619b422-ec75-4cd9-8c6f-0da1c271c804.jpg', 'Lợi ích:\r\n\r\nGiúp bạn di chuyển cùng thú cưng dễ dàng hơn, chỉ cần mang hoặc đeo lên vai như túi xách/balo đựng đồ bình thường.\r\nCó thể quan sát mọi hoạt động của bé trong khi đang vận chuyển.\r\nBalo có dây cầm chắc chắn, khóa kéo bền\r\nĐược làm từ những chất liệu thân thiện, không độc hại và dễ dàng lau chùi khi bám bẩn.\r\nHệ thống lỗ thoát khí tạo cảm giác thông thoáng cho vật nuôi mỗi khi nằm bên trong.\r\nGiúp bảo vệ thú cưng an toàn, thoải mái khi vận chuyển', 'Thương hiệu: Paddy\r\nPhù hợp cho: Chó/Mèo mọi lứa tuổi (tối đa 8kg)\r\nTúi vận chuyển chó mèo bằng lưới là một loại túi được thiết kế đặc biệt để vận chuyển chó mèo. Túi được làm bằng lưới, giúp chó mèo có thể nhìn thấy xung quanh và hít thở không khí trong lành. Túi vận chuyển có nhiều kích cỡ khác nhau, đơn giản và chắc chắn, phù hợp với mọi kích thước chó mèo. Với mẫu túi chó mèo này, chúng ta có thể đựng thú cưng vào và xách tay hoặc đeo chéo rất tiện lợi. \r\nLợi ích:\r\n\r\nGiúp bạn di chuyển cùng thú cưng dễ dàng hơn, chỉ cần mang hoặc đeo lên vai như túi xách/balo đựng đồ bình thường.\r\nCó thể quan sát mọi hoạt động của bé trong khi đang vận chuyển.\r\nBalo có dây cầm chắc chắn, khóa kéo bền\r\nĐược làm từ những chất liệu thân thiện, không độc hại và dễ dàng lau chùi khi bám bẩn.\r\nHệ thống lỗ thoát khí tạo cảm giác thông thoáng cho vật nuôi mỗi khi nằm bên trong.\r\nGiúp bảo vệ thú cưng an toàn, thoải mái khi vận chuyển.\r\nThành phần\r\n\r\nChất liệu: Chất liệu lưới nhựa PP thoáng mát, dễ giặt, nhanh khô và không gây kích ứng da của thú cưng.\r\nKích thước: \r\n+Size S (dài 35, cao 20, rộng 24 cm) : cho chó mèo dưới 3kg\r\n+Size M (dài 42, cao 24, rộng 26 cm): chó mèo dưới 5kg\r\n+Size L (dài 48, cao 26, rộng 28) : cho chó mèo dưới 8kg\r\nMàu sắc: màu ngẫu nhiên\r\nHướng dẫn sử dụng\r\n\r\nMở khóa kéo và cho thú cưng của bạn vào bên trong.\r\nKéo kín toàn bộ khóa kéo để tránh tuột hoặc vô tình làm tuột.\r\nKiểm tra lại thú cưng của bạn qua mặt lưới.\r\n#balochomeo #balovanchuyen #tuivanchuyen #balocho #balomeo #vanchuyenchomeo #tuichomeo #tuivanchuyenchomeo #phukienchomeo', 46, 1, 32, 9),
(23, 'Balo Cho Chó Mèo Phi Hành Gia Hình Mặt Mèo\r\n', 300000.00, 230000.00, 'https://paddy.vn/cdn/shop/files/6a4f5003-c99d-4be6-a96e-631acb7cefa1.jpg', 'Lợi ích:\r\n\r\nGiúp bạn và thú cưng dễ dàng đồng hành mọi nẻo đường\r\nĐế lót bên trong giúp cho balo chắc chắn, thú cưng có thể ngồi vững bên trong mà không lo chật.\r\nCó thể quan sát mọi hoạt động của bé trong khi đang vận chuyển.\r\nBalo có tay cầm cùng dây đeo chắc chắn, có khóa kéo bền\r\nSản phẩm được làm từ những chất liệu thân thiện, không độc hại và nhất là dễ dàng lau chùi khi bám bẩn.\r\nHệ thống lỗ thoát khí để giảm thiểu tối đa cảm giác bí bách cho vật nuôi mỗi khi nằm bên trong.', 'Thương hiệu: Paddy\r\nPhù hợp cho: Chó/Mèo mọi lứa tuổi (tối đa 10kg)\r\nBalo phi hành gia dành cho chó mèo là một vật dụng rất tiện lợi để di chuyển hoặc đi xa cùng với thú cưng của bạn. Nếu bạn đang có ý định đi phượt trên chiếc mô tô cùng bé mèo anh lông ngắn của mình, balo chó mèo sẽ là sự lựa chọn tuyệt vời cho bạn đấy. Sử dụng balo thú cưng sẽ giúp bạn không cần phải cầm xách và có thể dễ dàng quan sát bé qua ô kính. Với chất liệu da và nhựa trong suốt, balo phi hành gia nhẹ và mềm, có thể gấp lại và dễ dàng cất giữ. Balo được thiết kế nhỏ gọn, thông thoáng và có nhiều màu sắc cho bạn lựa chọn. \r\nLợi ích:\r\n\r\nGiúp bạn và thú cưng dễ dàng đồng hành mọi nẻo đường\r\nĐế lót bên trong giúp cho balo chắc chắn, thú cưng có thể ngồi vững bên trong mà không lo chật.\r\nCó thể quan sát mọi hoạt động của bé trong khi đang vận chuyển.\r\nBalo có tay cầm cùng dây đeo chắc chắn, có khóa kéo bền\r\nSản phẩm được làm từ những chất liệu thân thiện, không độc hại và nhất là dễ dàng lau chùi khi bám bẩn.\r\nHệ thống lỗ thoát khí để giảm thiểu tối đa cảm giác bí bách cho vật nuôi mỗi khi nằm bên trong.\r\nThành phần\r\n\r\nChất liệu: Vải và nhựa PVC cao cấp\r\nMàu sắc: Giao ngẫu nhiên hoặc vui lòng inbox cho Paddy để chọn màu.\r\nHướng dẫn sử dụng\r\n\r\nMở khóa kéo và cho thú cưng của bạn vào bên trong.\r\nKéo kín toàn bộ khóa kéo để tránh tuột hoặc vô tình làm tuột.\r\nKiểm tra lại thú cưng của bạn qua vòm kính hoặc lỗ thông gió 2 bên.', 23, 1, 67, 9),
(24, 'Balo Mèo Phi Hành Gia Hello Cat Cationic\r\n', 399000.00, 250000.00, 'https://paddy.vn/cdn/shop/files/balo-meo-cationic-cat-pack.webp', 'Lợi ích:\r\n\r\nLàm bằng vật liệu da cation, nhựa PVC, chất lượng cao, không thấm nước và dễ dàng vệ sinh, lau chùi.\r\nChất liệu vải oxford sáng màu không thấm nước, các lỗ phía ở hai bên được thiết kế thông gió cho thú cưng thở.\r\nDây đeo vai dày, êm, thoải mái, vì vậy người đeo sẽ cảm thấy thoải mái khi vận chuyển thú cưng.\r\nSản phẩm rất dễ dàng tháo lắp, vệ sinh.', 'Thương hiệu: Paddy\r\nPhù hợp cho: Chó/Mèo mọi lứa tuổi (mèo <7kg và chó <8kg)\r\nBalo mèo là một vật dụng rất tiện lợi để di chuyển hoặc đi xa cùng với thú cưng của bạn. Nếu bạn đang có ý định đi phượt trên chiếc mô tô cùng bé mèo anh lông ngắn, balo này sẽ là sự lựa chọn tuyệt vời cho bạn đấy. Sử dụng balo thú cưng sẽ giúp bạn không cần phải cầm xách và có thể dễ dàng quan sát bé mèo qua ô lưới. Với chất liệu da, balo nhẹ và mềm, có thể gấp lại và dễ dàng cất giữ.\r\nLợi ích:\r\n\r\nLàm bằng vật liệu da cation, nhựa PVC, chất lượng cao, không thấm nước và dễ dàng vệ sinh, lau chùi.\r\nChất liệu vải oxford sáng màu không thấm nước, các lỗ phía ở hai bên được thiết kế thông gió cho thú cưng thở.\r\nDây đeo vai dày, êm, thoải mái, vì vậy người đeo sẽ cảm thấy thoải mái khi vận chuyển thú cưng.\r\nSản phẩm rất dễ dàng tháo lắp, vệ sinh.\r\nThành phần\r\n\r\nChất liệu: Da cation, nhựa PVC\r\nKích thước: 32x28x37cm\r\nMàu sắc: Orange soda\r\nHướng dẫn sử dụng\r\n\r\nMở khóa kéo và cho thú cưng của bạn vào bên trong.\r\nKéo kín toàn bộ khóa kéo để tránh tuột hoặc vô tình làm tuột.\r\nKiểm tra lại thú cưng của bạn qua vòm kính hoặc lỗ thông gió 2 bên.', 23, 1, 12, 9),
(25, 'Cát Mèo Đậu Nành Cature Tofu Domestic 2.4kg (6L)\r\n', 200000.00, 140000.00, 'https://paddy.vn/cdn/shop/products/2_146dd2ee-204a-4c6c-88dc-820f88c4e6f9.jpg', 'Lợi ích:\r\n\r\nKiểm soát mùi cực mạnh: Với tính năng vón cục cực mạnh có thể khóa chặt \"chất thải vệ sinh\", vi khuẩn và mùi.\r\nĐược làm từ nguyên liệu tự nhiên không có thành phần độc hại, sử dụng chất kết dính thực vật tự nhiên, không hóa chất, không phẩm màu.\r\n100% phân hủy sinh học, thân thiện với môi trường.\r\nSiêu tiết kiệm, hấp thụ 400%.\r\nKhông gây bụi trong không khí: Với 3 quy trình loại bỏ bụi và công nghệ hút chân không đảm bảo không có bụi trong quá trình sử dụng, giúp an toàn hệ hô hấp và tiết niệu cho boss.\r\nVón cục nhanh chóng\r\nTrọng lượng nhẹ cực kỳ tiện lợi', 'Thương hiệu: Cature\r\nPhù hợp cho: Mèo mọi lứa tuổi\r\nCát đậu nành Cature Tofu Domestic dành cho mèo được làm từ bã đậu nành với nhiều công dụng đảm bảo sức khoẻ cho mèo cũng như đáp ứng tiêu chí bảo vệ môi trường. Tính chất đặc biệt của cát Cature là vón cục nhanh, dễ phân huỷ nên có thể tiêu huỷ trong toilet. Cát mèo này còn có đặc tính thấm hút tốt, không bụi, khoá vi trùng hiệu quả mang đến cảm giác dễ chịu cho mèo, thân thiện với người dùng.\r\nLợi ích:\r\n\r\nKiểm soát mùi cực mạnh: Với tính năng vón cục cực mạnh có thể khóa chặt \"chất thải vệ sinh\", vi khuẩn và mùi.\r\nĐược làm từ nguyên liệu tự nhiên không có thành phần độc hại, sử dụng chất kết dính thực vật tự nhiên, không hóa chất, không phẩm màu.\r\n100% phân hủy sinh học, thân thiện với môi trường.\r\nSiêu tiết kiệm, hấp thụ 400%.\r\nKhông gây bụi trong không khí: Với 3 quy trình loại bỏ bụi và công nghệ hút chân không đảm bảo không có bụi trong quá trình sử dụng, giúp an toàn hệ hô hấp và tiết niệu cho boss.\r\nVón cục nhanh chóng\r\nTrọng lượng nhẹ cực kỳ tiện lợi\r\nThành phần\r\n\r\nThành phần chính là bã đậu nành.\r\nCó 2 loại chính là: Đậu nành, đậu nành mix gỗ\r\nHướng dẫn sử dụng\r\n\r\nĐổ cát mèo vào nhà vệ sinh của mèo của bạn. Độ dày lớp cát khoảng 5-7cm.\r\nGiữ cho lớp cát luôn sạch sẽ bằng cách thường xuyên loại bỏ phân và vắt sạch cát thải.\r\nNếu bạn muốn tăng hiệu quả khử mùi của cát mèo, bạn có thể sử dụng các sản phẩm khử mùi.\r\nKhi cát mèo đã bị bẩn hoặc hết hạn sử dụng, bạn nên thay thế bằng lượng cát mới để giữ cho nhà vệ sinh của mèo luôn sạch sẽ và giảm thiểu mùi hôi.\r\n*Lưu ý: để đảm bảo chất lượng của cát mèo, bạn nên lưu trữ nó ở nơi khô ráo và thoáng mát. Nếu bạn sử dụng cát mèo lần đầu tiên, hãy giới thiệu nó cho mèo của bạn bằng cách đổ một lượng nhỏ vào nhà vệ sinh và cho mèo khám phá.', 49, 1, 21, 10),
(26, 'Dung Dịch Men Tiêu Hoá Cho Chó Mèo Q8 Satic 20ml\r\n', 50000.00, 45000.00, 'https://paddy.vn/cdn/shop/files/dung-dich-men-tieu-hoa-cho-cho-meo-q8-satic-20ml.png', 'Lợi ích:\r\n\r\nKích thích tiêu hoá, tăng tính them ăn cho chó, mèo.\r\nTăng khả năng hấp thụ thức ăn, giúp hấp thụ triệt để thức ăn.\r\nGiảm mùi hôi của phân thú cưng.\r\nTạo môi trường thuận lợi cho vi khuẩn có lợi phát triển và tạo môi trường bất lợi cho vi khuẩn có hại.\r\nHạn chế tiêu chảy.\r\nTăng sức đề kháng.', 'Thương hiệu: Q8\r\nPhù hợp cho: Chó/Mèo mọi lứa tuổi\r\nMen Tiêu Hoá Cho Chó Mèo Q8 Satic là một loại dung dịch chăm sóc sức khỏe cho chó mèo dạng nước, có tác dụng hỗ trợ tiêu hóa thức ăn, giúp chó mèo hấp thụ chất dinh dưỡng tốt hơn. Q8 Satic có thành phần an toàn, lành tính, dễ sử dụng, phù hợp với nhiều đối tượng chó mèo, được đánh giá cao về chất lượng và hiệu quả.\r\nLợi ích:\r\n\r\nKích thích tiêu hoá, tăng tính them ăn cho chó, mèo.\r\nTăng khả năng hấp thụ thức ăn, giúp hấp thụ triệt để thức ăn.\r\nGiảm mùi hôi của phân thú cưng.\r\nTạo môi trường thuận lợi cho vi khuẩn có lợi phát triển và tạo môi trường bất lợi cho vi khuẩn có hại.\r\nHạn chế tiêu chảy.\r\nTăng sức đề kháng.\r\nThành phần\r\n\r\nEnzyme amylase, Enzyme protease, Enzyme lipase, Lactobacillus acidophilus, Enterococcus faecium, Bifidobacterium bifidum.\r\nHướng dẫn sử dụng\r\n\r\nChó mèo ở mọi lứa tuổi, đặc biệt là chó mèo con đang trong giai đoạn phát triển, chó mèo già yếu, chó mèo bị rối loạn tiêu hóa.\r\nNhỏ trực tiếp vào miệng hoặc trộn cùng thức ăn, hòa vào nước uống. Liều lượng và thời gian điều trị phụ thuộc vào tình trạng của từng thú cưng.\r\nNếu thú cưng có bất kỳ phản ứng phụ nào sau khi sử dụng sản phẩm, hãy ngừng sử dụng và đưa thú cưng đến gặp bác sĩ thú y ngay lập tức.', 67, 1, 304, 8),
(27, 'Dung Dịch Trợ Lực Cho Chó Mèo Q8 Satosal 20ml\r\n', 68000.00, 65000.00, 'https://paddy.vn/cdn/shop/files/dung-dich-tro-luc-cho-cho-meo-q8-satosal-20ml_5.png', 'Lợi ích:\r\n\r\nHỗ trợ tăng cường sức khỏe, nâng cao sức đề kháng cho chó mèo.\r\nGiúp bồi bổ cơ thể, giúp chó mèo nhanh chóng phục hồi sức khỏe sau khi ốm.\r\nGiúp tăng cường khả năng hấp thụ chất dinh dưỡng, giúp chó mèo phát triển khỏe mạnh.', 'Thương hiệu: Q8\r\nPhù hợp cho: Chó/Mèo mọi lứa tuổi\r\nDung Dịch Trợ Lực Cho Chó Mèo Q8 Satosal là một loại dung dịch chăm sóc sức khỏe cho chó mèo dạng nước, có tác dụng tăng cường sức khỏe, nâng cao sức đề kháng cho chó mèo. Sản phẩm có thành phần an toàn, lành tính, dễ sử dụng, phù hợp với nhiều đối tượng chó mèo, được đánh giá cao về chất lượng và hiệu quả.\r\nLợi ích:\r\n\r\nHỗ trợ tăng cường sức khỏe, nâng cao sức đề kháng cho chó mèo.\r\nGiúp bồi bổ cơ thể, giúp chó mèo nhanh chóng phục hồi sức khỏe sau khi ốm.\r\nGiúp tăng cường khả năng hấp thụ chất dinh dưỡng, giúp chó mèo phát triển khỏe mạnh.\r\nThành phần\r\n\r\nVitamin, Biotin, L-Lysine, L-Arginine, L-Glutamine, Taurine, Choline bitartrate, L-Carnitine, L-Ascorbyl-2-polyphosphate, Selenium, Kẽm, Đồng, Mangan, Iốt, Selen...\r\nHướng dẫn sử dụng\r\n\r\nChó mèo ở mọi lứa tuổi, đặc biệt là chó mèo con đang trong giai đoạn phát triển, chó mèo già yếu, chó mèo bị suy nhược cơ thể.\r\nNhỏ trực tiếp vào miệng hoặc trộn cùng thức ăn, hòa vào nước uống. Liều lượng và thời gian điều trị phụ thuộc vào tình trạng của từng thú cưng.\r\nNếu thú cưng có bất kỳ phản ứng phụ nào sau khi sử dụng sản phẩm, hãy ngừng sử dụng và đưa thú cưng đến gặp bác sĩ thú y ngay lập tức.', 45, 1, 12, 8),
(28, 'Dung Dịch Giải Độc Gan Thận Cho Chó Mèo Q8 Boganox 20ml\r\n', 60000.00, 50000.00, 'https://paddy.vn/cdn/shop/files/dung-dich-giai-doc-gan-than-cho-cho-meo-q8-boganox-20ml.png', 'Lợi ích:\r\n\r\nHỗ trợ giải độc gan thận cho chó mèo.\r\nGiúp bảo vệ gan thận, tăng cường chức năng gan thận.\r\nHỗ trợ giảm thiểu các triệu chứng của suy gan thận như vàng da, nước tiểu vàng, chán ăn, mệt mỏi,...', 'Thương hiệu: Q8\r\nPhù hợp cho: Chó/Mèo mọi lứa tuổi\r\nGiải Độc Gan Thận Cho Chó Mèo Q8 Boganox là một loại dung dịch chăm sóc sức khỏe cho chó mèo dạng nước, có tác dụng hỗ trợ giải độc gan thận cho chó mèo. Q8 Boganox là sản phẩm được đánh giá cao về chất lượng và hiệu quả, có thành phần an toàn, lành tính, dễ sử dụng, phù hợp với nhiều đối tượng chó mèo.\r\nLợi ích:\r\n\r\nHỗ trợ giải độc gan thận cho chó mèo.\r\nGiúp bảo vệ gan thận, tăng cường chức năng gan thận.\r\nHỗ trợ giảm thiểu các triệu chứng của suy gan thận như vàng da, nước tiểu vàng, chán ăn, mệt mỏi,...\r\nThành phần\r\n\r\nL-Arginine, L-Glutamine, Taurine, Silymarin, Vitamin B1, Vitamin B6, Vitamin B12\r\nHướng dẫn sử dụng\r\n\r\nChó mèo bị suy gan thận, nhiễm độc gan thận do thuốc, hóa chất, thực phẩm, viêm gan, xơ gan, thận hư.\r\nNhỏ trực tiếp vào miệng hoặc trộn cùng thức ăn, hòa vào nước uống. Liều lượng và thời gian điều trị phụ thuộc vào tình trạng của từng thú cưng.\r\nNếu thú cưng có bất kỳ phản ứng phụ nào sau khi sử dụng sản phẩm, hãy ngừng sử dụng và đưa thú cưng đến gặp bác sĩ thú y ngay lập tức.', 100, 1, 456, 7),
(30, 'Xịt Trị Ve Rận Cho Chó Mèo Q8 Santox 50ml\r\n', 59000.00, 55000.00, 'https://paddy.vn/cdn/shop/files/xit-tri-ve-ran-cho-cho-meo-q8-santox-50ml.png', 'Lợi ích:\r\n\r\nNgăn ngừa và phòng bọ chét, bọ, ve, chấy, rận, ghẻ,… ở chó mèo\r\nBảo vệ thú cưng khoẻ mạnh và luôn sạch sẽ', 'Thương hiệu: Q8\r\nPhù hợp cho: Chó/Mèo mọi lứa tuổi\r\nXịt Trị Ve Rận Cho Chó Mèo Q8 Santox là một loại dung dịch chăm sóc sức khỏe cho chó mèo dạng nước, hiệu quả trong việc diệt trừ ve rận, bọ chét, ghẻ, nấm cho chó mèo. Cần lưu ý tránh xịt vào mắt, mũi, miệng của thú cưng và không sử dụng cho thú cưng đang mang thai hoặc cho con bú.\r\nLợi ích:\r\n\r\nNgăn ngừa và phòng bọ chét, bọ, ve, chấy, rận, ghẻ,… ở chó mèo\r\nBảo vệ thú cưng khoẻ mạnh và luôn sạch sẽ\r\nThành phần\r\n\r\nPyrethroid 37.5mg, Dung môi và chất phụ vừa đủ 50ml\r\nHướng dẫn sử dụng\r\n\r\nPhun trung bình: Chó 10-12 lần/kg và Mèo 5-6 lần/kg\r\nLắc kỹ bình trước khi phun\r\nGiữ bình thẳng đứng, phun ngược chiều lông thú, phun khoảng cách với cơ thể 20-30 cm\r\nPhun đều toàn thân, mang găng tay xoa khắp cơ thể, cả phần đầu và ngóc ngách cơ thể nhất là nơi có ký sinh trùng lưu trú\r\nBảo quản nơi khô ráo, thoáng mát. Tránh ánh nắng trực tiếp', 56, 1, 123, 11),
(31, 'Dung Dịch Bổ Sung Canxi Cho Chó Mèo Q8 Canxi Nano Plus 20ml\r\n', 70000.00, 65000.00, 'https://paddy.vn/cdn/shop/files/dung-dich-bo-sung-canxi-cho-cho-meo-canxi-nano-plus-20ml.png', 'Lợi ích:\r\n\r\nBổ sung canxi, vitamin D3, vitamin K2, magie, kẽm cho chó mèo.\r\nGiúp xương chắc khỏe, phòng ngừa loãng xương.\r\nHỗ trợ phát triển chiều cao ở chó mèo con.\r\nTăng cường sức khỏe, nâng cao sức đề kháng cho chó mèo.', 'Thương hiệu: Q8\r\nPhù hợp cho: Chó/Mèo mọi lứa tuổi (bị còi xương, suy dinh dưỡng, gãy xương, loãng xương)\r\nQ8 Canxi Nano Plus là một loại dung dịch chăm sóc sức khỏe cho chó mèo dạng nước, có tác dụng bổ sung canxi cho chó mèo. Q8 Canxi Nano Plus có thành phần an toàn, lành tính, dễ sử dụng, phù hợp với nhiều đối tượng chó mèo, được đánh giá cao về chất lượng và hiệu quả.\r\nLợi ích:\r\n\r\nBổ sung canxi, vitamin D3, vitamin K2, magie, kẽm cho chó mèo.\r\nGiúp xương chắc khỏe, phòng ngừa loãng xương.\r\nHỗ trợ phát triển chiều cao ở chó mèo con.\r\nTăng cường sức khỏe, nâng cao sức đề kháng cho chó mèo.\r\nThành phần\r\n\r\nCanxi nano: 200 mg, Vitamin D3: 50 IU, Vitamin K2: 10 mcg, Magie: 10 mg, Kẽm: 1 mg\r\nHướng dẫn sử dụng\r\n\r\nNhỏ trực tiếp vào miệng hoặc trộn cùng thức ăn, hòa vào nước uống. Liều lượng và thời gian điều trị phụ thuộc vào tình trạng của từng thú cưng.\r\nNếu thú cưng có bất kỳ phản ứng phụ nào sau khi sử dụng sản phẩm, hãy ngừng sử dụng và đưa thú cưng đến gặp bác sĩ thú y ngay lập tức.', 35, 1, 76, 7);
