INSERT INTO `services` (`ID`, `Name`, `Image`, `Price`, `Detail`, `Time_Service`, `Category_ID`, `Status`) VALUES
(3, 'Khách Sạn Thú Cưng Phòng Thường', 'public/img/Service/Khách Sạn Thú Cưng Phòng Thường.png', 130000.00, 'Ngoài cung cấp phụ kiện và thức ăn cho chó mèo thì Furry Friends còn có DỊCH VỤ KHÁCH SẠN MÈO giúp những bạn Sen bận đi về quê hoặc du lịch.\r\nPhòng thường: 130k/ngày (Khay vệ sinh bên trong phòng). Tối đa 2 bé/1 phòng.\r\nKhi gửi 2 - 5 bé chung 1 phòng thì bé thứ 2 trở đi sẽ phụ thu 50% giá phòng/bé.\r\nDỊCH VỤ BAO GỒM\r\nDọn Vệ Sinh Mỗi Ngày\r\nThức Ăn Hạt Mĩ Poyal Canin - Cat\'s Eye - Natural Core.\r\nMỗi Bé 1 Cử Pate/Ngày\r\n<p style=\"color:red;\">CHÍNH SÁCH HỦY PHÒNG</p>\r\nQuý khách hủy phòng trong vòng 1 tuần trước ngày check-in: Furry Friends sẽ không hoàn cọc (mất 100% cọc)\r\nQuý khách hủy phòng trước ngày check-in từ 8 - 14 ngày: hoàn 50% phí đặt cọc.\r\nQuý khách hủy phòng trước 14 ngày: được hoàn 100% phí đặt cọc.\r\nCHÍNH SÁCH HỦY PHÒNG TRONG DỊP TẾT NGUYÊN ĐÁN:\r\nQuý khách hủy phòng trong vòng 14 ngày trước ngày check-in: Furry Friends sẽ không hoàn cọc (mất 100% cọc).\r\nQuý khách hủy phòng trước 14 ngày: được hoàn 50% phí đặt cọc.', '24:00:00', 17, 1),
(4, 'Dịch Vụ Khách Sạn Phòng VIP', 'public/img/Service/Dịch Vụ Khách Sạn Phòng VIP.png', 160000.00, 'Phòng VIP: 160k/ngày (Phòng vệ sinh ngăn cách riêng biệt trong phòng). Tối đa 3 bé/1 phòng.\r\nDỊCH VỤ BAO GỒM\r\nDọn Vệ Sinh Mỗi Ngày\r\nThức Ăn Hạt Mĩ Poyal Canin - Cat\'s Eye - Natural Core.\r\nMỗi Bé 1 Cử Pate/Ngày\r\nCHÍNH SÁCH HỦY PHỎNG\r\nQuý khách hủy phòng trong vòng 1 tuần trước ngày check-in: Furry Friends sẽ không hoàn cọc (mất 100% cọc)\r\nQuý khách hủy phòng trước ngày check-in từ 8 - 14 ngày: hoàn 50% phí đặt cọc.\r\nQuý khách hủy phòng trước 14 ngày: được hoàn 100% phí đặt cọc.\r\nCHÍNH SÁCH HỦY PHÒNG TRONG DỊP TẾT NGUYÊN ĐÁN:\r\nQuý khách hủy phòng trong vòng 14 ngày trước ngày check-in: Furry Friends sẽ không hoàn cọc (mất 100% cọc).\r\nQuý khách hủy phòng trước 14 ngày: được hoàn 50% phí đặt cọc.', '24:00:00', 17, 1),
(5, 'Khách Sạn Thú Cưng Phòng VVIP', 'public/img/Service/Khách Sạn Thú Cưng Phòng VVIP.png', 210000.00, 'Phòng VIP: 210k/ngày (Phòng vệ sinh ngăn cách riêng biệt trong phòng). Tối đa 3 bé/1 phòng.\r\nDỊCH VỤ BAO GỒM\r\nDọn Vệ Sinh Mỗi Ngày\r\nThức Ăn Hạt Mĩ Poyal Canin - Cat\'s Eye - Natural Core.\r\nMỗi Bé 1 Cử Pate/Ngày\r\nCHÍNH SÁCH HỦY PHỎNG\r\nQuý khách hủy phòng trong vòng 1 tuần trước ngày check-in: Furry Friends sẽ không hoàn cọc (mất 100% cọc)\r\nQuý khách hủy phòng trước ngày check-in từ 8 - 14 ngày: hoàn 50% phí đặt cọc.\r\nQuý khách hủy phòng trước 14 ngày: được hoàn 100% phí đặt cọc.\r\nCHÍNH SÁCH HỦY PHÒNG TRONG DỊP TẾT NGUYÊN ĐÁN:\r\nQuý khách hủy phòng trong vòng 14 ngày trước ngày check-in: Furry Friends sẽ không hoàn cọc (mất 100% cọc).\r\nQuý khách hủy phòng trước 14 ngày: được hoàn 50% phí đặt cọc.', '24:00:00', 17, 1),
(6, 'Spa - Grooming', 'public/img/Service/Spa - Grooming.png', 100000.00, 'Spa - Grooming\r\nGói tắm vệ sinh cho chó đã bao gồm cắt móng, mài móng, cạo lông đệm chân, vệ sinh tai, vắt tuyến hôi và tắm.\r\nGói tắm vệ sinh cho mèo đã bao gồm cắt móng, cạo lông đệm chân, vệ sinh tai và tắm.', '01:00:00', 16, 1);

